func joinBaseFooter(base, suffix string) string {
	base = strings.TrimSpace(base)
	if base == "" || base == "/" {
		return "/" + strings.TrimPrefix(suffix, "/")
	}
	return strings.TrimRight(base, "/") + "/" + strings.TrimPrefix(suffix, "/")
}

